                {
                    "instance": "SampleManufacturer.CoffeeMaker.CleanMode",
                    "interface": "Alexa.ToggleController",
                    "type": "AlexaInterface",
                    "version": "3",
                    "capabilityResources": {
                        "friendlyNames": [
                            {
                                "@type": "text",
